    "rxToggleSwitch.async": {
        "markup": "<div ng-controller=\"rxToggleSwitchAsyncCtrl\">\n  <p>\n    <h4>Successful Operation</h4>\n    <rx-toggle-switch \n      class=\"demo-post-hook\"\n      ng-model=\"toggle3\"\n      post-hook=\"attemptChange(value)\"\n      ng-disabled=\"loading\">\n    </rx-toggle-switch>\n  </p>\n  <p>\n    <h4>Failed Operation</h4>\n    <rx-toggle-switch \n      class=\"demo-failed-async\"\n      ng-model=\"toggle5\"\n      post-hook=\"attemptFailedChange()\"\n      true-value=\"active\"\n      ng-disabled=\"loading5\">\n    </rx-toggle-switch>\n  </p>\n</div>\n",
        "javascript": "angular.module('demoApp')\n.controller('rxToggleSwitchAsyncCtrl', function ($scope, $timeout, rxNotify) {\n    $scope.toggle3 = true;\n    $scope.toggle5 = true;\n\n    $scope.attemptChange = function () {\n        $scope.loading = true;\n        rxNotify.clear('page');\n        rxNotify.add('Saving...', {\n            loading: true\n        });\n\n        // Simulate an API request\n        $timeout(function () {\n            $scope.loading = false;\n            rxNotify.clear('page');\n            rxNotify.add('Change saved', {\n                type: 'success'\n            });\n        }, 1000);\n    };\n\n    $scope.attemptFailedChange = function (value) {\n        $scope.loading5 = true;\n        rxNotify.clear('page');\n        rxNotify.add('Attempting to activate...', {\n            loading: true\n        });\n\n        // Simulate a failed API request\n        $timeout(function () {\n            $scope.loading5 = false;\n            rxNotify.clear('page');\n            rxNotify.add('Asynchronous operation failed', {\n                type: 'error',\n            });\n\n            // Reset toggle switch to original value to simulate failed async operation\n            $scope.toggle5 = !value;\n        }, 1000);\n    };\n});\n"
    },
