    "rxSelectFilter.simple": {
        "markup": "<div ng-controller=\"rxSelectFilterSimpleCtrl\">\n  <h3>Filtered Table</h3>\n  <table class=\"table-striped\">\n    <thead>\n      <th colspan=\"3\" class=\"filter-header\">\n        <form rx-form>\n          <rx-form-section controlled-width>\n            <rx-select-filter filter=\"filter\"></rx-select-filter>\n          </rx-form-section>\n        </form>\n      </th>\n      <tr>\n        <th>Account</th>\n        <th>Status</th>\n        <th>Description</th>\n      </tr>\n    </thead>\n    <tbody>\n      <tr ng-repeat=\"ticket in (filteredTickets = (tickets | Apply:filter))\">\n        <td>{{ticket.account}}</td>\n        <td>{{ticket.status}}</td>\n        <td>{{ticket.description}}</td>\n      </tr>\n      <tr ng-if=\"filteredTickets.length === 0\">\n        <td colspan=\"6\">\n          <div ng-if=\"tickets.length === 0\">No tickets were found.</div>\n          <div ng-if=\"tickets.length !== 0\">No results match those criteria.</div>\n        </td>\n      </tr>\n    </tbody>\n  </table>\n</div>",
        "javascript": "angular.module('demoApp')\n.controller('rxSelectFilterSimpleCtrl', function ($scope, SelectFilter) {\n    $scope.filter = SelectFilter.create({\n        properties: ['account', 'status'],\n        selected: {\n            account: ['A']\n        }\n    });\n\n    $scope.tickets = [\n        { account: 'A', status: 'NEW', description: 'A new ticket' },\n        { account: 'A', status: 'IN_PROGRESS', description: 'Fix all the bugs' },\n        { account: 'B', status: 'TRANSFERRED', description: 'Don\\'t stop believing' },\n        { account: 'B', status: 'VENDOR', description: 'Hold on to that feeling' },\n        { account: 'A', status: 'TRANSFERRED', description: 'qwertyuiop' }\n    ];\n});\n"
    },
